sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue === '') {
		return;
	}

	var fieldName = 'u_what_needed';
	var whatneeded = g_form.getValue(fieldName);

	//clear all of the choices from the what needed field choice list
	g_form.clearOptions(fieldName);

	if (newValue === 'hr') {
		g_form.addOption(fieldName, 'hr1', 'Human Resources 1');
		g_form.addOption(fieldName, 'hr2', 'Human Resources 2');
	}

	if (newValue === 'facilities') {
		g_form.addOption(fieldName, 'facilities1', 'Facilities 1');
		g_form.addOption(fieldName, 'facilities2', 'Facilities 2');
	}

	if (newValue === 'legal') {
		g_form.addOption('u_what_needed','legal1','Legal 1');
		g_form.addOption('u_what_needed','legal2','Legal 2');
	}

	//without condition, because this fild is needed in every options-field
	g_form.addOption(fieldName, 'other', 'Other');

	if (isLoading && !g_form.isNewRecord()) {
		g_form.setValue(fieldName, whatneeded);
	}
}
